// MOOC - Programmer un objet avec Arduino
// TP 1 : Feu tricolore 
//
// Pin 13 has the red led connected.
// Pin 12 has the orange connected.
// Pin 11 has the green led connected.

// Give them names
int LedRed = 13;
int LedOrange = 12;
int LedGreen = 11;

// Give time period for turning on (in ms)
int LedRedTurningOn = 3000;
int LedOrangeTurningOn = 1000;
int LedGreenTurningOn = 3000;

// Setup routine
void setup() 
{
  // Initialize the digital pins as outputs.
  pinMode(LedRed, OUTPUT);
  pinMode(LedOrange, OUTPUT);
  pinMode(LedGreen, OUTPUT);

  // Turn off all leds
  digitalWrite(LedOrange, LOW);   // turn the orange led off
  digitalWrite(LedRed, LOW);   // turn the red led off
  digitalWrite(LedGreen, LOW);   // turn the green led off
}

// the loop routine
