const int GoPieton = 8; 
const int ButtonPin = 2; 

int AppelPieton = 0; 
int TotalVert = 3000 ;
int TempsEcoule = 0 ;
int deltaTemps = 1 ;

void setup() 
{
	// 
	pinMode(rouge, OUTPUT);
	pinMode(orange, OUTPUT);
	pinMode(vert, OUTPUT);
	pinMode(StopPieton, OUTPUT);
	pinMode(GoPieton, OUTPUT);
	pinMode(ButtonPin, INPUT); 
}

void loop() 
{

	AppelPieton = digitalRead(ButtonPin);     		

	digitalWrite(vert, HIGH); 			// vert voiture allumé
	digitalWrite(StopPieton, HIGH); 
	delay(deltaTemps) ;
	TempsEcoule = TempsEcoule + deltaTemps ; // on mesure le temps qui s'écoule 

